import React, {useState} from 'react';
import './../../../App.css';
import './../../Main.css';
import moment from 'moment';

export const MessageBoard = () => {
    const username = "Alex";
    const messages = ["OK", "OKo", "OKp", "ooooOK", "OkkkK", "OK", "OK",];

    return(
        <div>
            <MessageSection username={username} messages={messages} />
        </div>
    );
};


const MessageSection = (props) => {
    const date = new Date()
    const firstMessages = props.messages.slice(0,1);
    const otherMessages = props.messages.slice(1);

    return(
        <div>
            <MessageHeader date={date} username={props.username} message={firstMessages}/>
            <UserMessages messages={otherMessages}/>
        </div>
    );
};

const  UserIcon = (props) => {
    const getFirstUserInitial = props.username.charAt(0);
    return (
        <div className="user-message-avatar">
            <span className="user-message-initial">{getFirstUserInitial}</span>
        </div>
    );
}

const ChatUserName = (props) => {
    return <span className="user-message-username">{props.username} <span className="user-message-timestamp">{moment(props.time).format("LT")}</span></span>;
}

const MessageHeader = (props) => {
    const messages = props.messages;

    return (
        <div>
            <UserIcon username={"Alex"}/>
            <ChatUserName username={"Alex"} time={props.date}/>
            <UserMessages messages={messages}/>
        </div>
    );
}

const UserMessages = (props) => {
    const messages = props.messages;
    return(
        <div className="user-message">
            {messages}
        </div>
    );
}


const FirstMessage = (props) => {
