        </div>
    );
}

const TimeSeperator = (props) => {

    let shouldDisplay = false;

    const timeBetweenMessages = (props) => {

        const timeBetweenLastMessage = moment.duration(props.messageTime.diff(props.lastMessage, 'days'));

        if (timeBetweenLastMessage > 1) {
            shouldDisplay = true
        }
    }

    return(
        <div>
            {shouldDisplay ? <div>-- HERE WE WILL HAVE DATE SEPERATOR --</div> : null}

        </div>
    );
}
