import React, {useState} from 'react';
import { Check } from './Icon';
import './../Main.css';

export const Toggle = (props) => {

    const [selected, setSelected] = useState(false);

    const test = () => {
        const opposite = !selected;
        setSelected(opposite)
    }

    return(
        <div className={ selected ? 'toggle__container toggle__container--selected ' : 'toggle__container' } onClick={test}>
            <div className='toggle__check'> {selected ? <Check /> : ''} </div>
            <div className={ selected ? ' toggle__childSwitch toggle__childSwitch--selected toggle__container--selected ' : 'toggle__childSwitch' }>
            </div>
        </div>
    );
};
