import React, {useState} from 'react';
import './Login.css';
import { Eye, EyeSlash } from './Icon.js';

export const Login = () => {

    const [signUp, setSignUp] = useState(false);
    const [fieldsEmpty, setFieldsEmpty] = useState(true);

    const showSignUp = () => {
        return setSignUp(true)
    };

    const showLoginIn = () => {
        return setSignUp(false)
    };

    return (
        <div class="login">
            <Slackish></Slackish> 
            {signUp ? <SignUpForm goBackTo={showLoginIn}></SignUpForm> : <LoginForm showSignUp={showSignUp}></LoginForm>}
        </div>
    );
};

const Slackish = () => {
    return (
        <div class="text__container">
            <span class='login__title'> #SLACKISH </span>
        </div>
    );
};

const LoginForm = (props) => {
    const [hidePassword, setHidePassword] = useState('password');

    const hidePasswordToggle = () => {
        console.log(hidePassword === 'text');
        return hidePassword === 'text' ? setHidePassword('password') : setHidePassword('text');
    }

    return (  
        <div>
            <TextField placeholder={'Username/Email'}></TextField>
            <PasswordField 
            type={hidePassword} 
            placeholder={'Password'}
            hidePasswordToggle={hidePasswordToggle}
            hidePassword={hidePassword}
            ></PasswordField>
            <LoginButton label={'Sign In'}></LoginButton>
            <SignUpButton showSignUp={props.showSignUp}></SignUpButton>
        </div>
    );
};

const SignUpForm = (props) => {

    const [hidePassword, setHidePassword] = useState('password');

    const hidePasswordToggle = () => {
        console.log(hidePassword === 'text');
        return hidePassword === 'text' ? setHidePassword('password') : setHidePassword('text');
    }

    return (  
        <div class="test1">
            <TextField placeholder={'Username'}></TextField>
            <TextField placeholder={'Email'}></TextField>
            <PasswordField class="login__field--signUp" 
            type={hidePassword} 
            placeholder={'Password'}
            hidePasswordToggle={hidePasswordToggle}
            hidePassword={hidePassword}
            ></PasswordField>
            <PasswordField class="login__field--signUp" 
            type={hidePassword} 
            placeholder={'Confirm password'}
            hidePasswordToggle={hidePasswordToggle}
            hidePassword={hidePassword}
            ></PasswordField>
            <LoginButton label={'Sign me up!'}></LoginButton>
            <CancelButton goBackTo={props.goBackTo}></CancelButton>
        </div>
    );
};

const TextField = (props) => {
    return (
        <div class="container__textField">
            <input
            type={props.type}
            class='login__field'
            placeholder={props.placeholder}>
            </input>
        </div>
    );
};

const PasswordField = (props) => {
    return (
        <div class="container__textField">
            <input
            type={props.type}
            class='login__field'
            placeholder={props.placeholder}>
            </input>
            <span class="login__icon" onClick={props.hidePasswordToggle}> {props.hidePassword === 'text' ? <Eye></Eye> : <EyeSlash></EyeSlash>} </span>
        </div>
    );
};

const LoginButton = (props) => {
    return (
        <button class="login__button login__button--email">
            {props.label}
        </button>
    );
}

const CancelButton = (props) => {
    return (
        <div>
            <button class="login__button login__button--cancel" onClick={props.goBackTo}>
                Cancel
            </button>
        </div>
    );
}

const SignUpButton = (props) => {
    return(
        <p>
            Don't have an account? <span class="link" onClick={props.showSignUp}>Create one now!</span>
        </p>
    );
};
