import React, {useState} from 'react';
import './Login.css';

export const Login = () => {

    const [signUp, setSignUp] = useState(false);

    return (
        <div class="login">
            <Slackish></Slackish> 
            <UsernameField></UsernameField>
            <PasswordField></PasswordField>
            <LoginButton></LoginButton>
            <SignUpButton></SignUpButton>
        </div>
    );
};

const Slackish = () => {
    return (
        <div class="text__container">
            <span class='login__title'> #SLACKISH </span>
        </div>
    );
}

const UsernameField = () => {
    return (
        <input
        class='login__field'
        placeholder='Username/email'> 
        </input>
    )
};

const PasswordField = () => {
    return (
        <input
        type="password"
        class="login__field"
        placeholder='Password'> 
        </input>
    )
};

const LoginButton  = () => {
    return (
        <button class="login__button login__button--email">
            Sign In
        </button>
    );
}

const SignUpButton = () => {

    return(
        <p>
            Don't have an account? <a class="link">Create one now!</a>
        </p>
    )
}
