import React, { useState } from 'react';
import './../../App.css';
import { PlusSign, Times}  from './../shared/Icon';
import { BasicButton }  from './../shared/BasicButton';
import { FloatingLabelTextField } from './../shared/FloatingLabelTextField';

export const ChannelSection = () => {

    const [channels, setChannels] = useState(['General', 'General test', 'General test', 'General test', 'General test', 'General test', 'General test', 'General test', 'General test', 'General test', 'General test', 'General test', 'General test']);
    const [name, setName] = useState('');
    const [showOverlay, setShowOverlay] = useState(false);
    const [privateChannel, setPrivateChannel] = useState(false);
    const [disabled, setDisabled] = useState(true);

    const click = () => {
        setShowOverlay(true);
    };

    const close = () => {
        setShowOverlay(false);
    };

    const onChannelNameChange= (event) => {
        setName(event.target.value);
        if(event.target.value.length > 0) {
            setDisabled(false);
        }
    };

    const onSubmit = () => {
        const channelsTemp = channels
        channelsTemp.push(name)
        setChannels(channelsTemp);
        close(); //close pop-up after adding channel
    };

    return(
        <div className="main__layout--channels">
            <CreateChannelButton 
            channelsCount={channels.length} 
            createChannelClick={click}
            />
            <Channels channels={channels} />
           { showOverlay ? <PopUpAddChannel 
           disabled={disabled}
           closeOverlay={close} 
           onChange={onChannelNameChange} 
           onSubmit={onSubmit}
           /> : null }
        </div>
    );
};

const CreateChannelButton = (props) => {
    return (
        <div className="longButton__header">
            <span className="label__header label__header--title " > Channels ({props.channelsCount})</span> 
            <span className="label__header" onClick={props.createChannelClick}><PlusSign /></span>
        </div>
    );
};

const Channel = (props) => {
    return (
        <div className="section__channel containner_channel">
            {props.name} Channel
        </div>
    );
}


const Channels = (props) => {
const channelsAr = props.channels;
    return (
        <div className="section__channels">
            { channelsAr.map((aChannel) => 
                <Channel name={aChannel} />
            )}
        </div>
    );
}

const PopUpAddChannel = (props) => {
    const emptyFieldMessage = 'Donâ€™t forget to name your channel.';
    const channelDescription = 'Channels are where your members communicate. They\'re best when organized around a topic - #proj-budget, for example.'
    return(
        <div className="section__overlay" >
            <div className="popUp">
                <div>
                    <span className="popUp__header">Create a channel</span> 
                    <span className='popUp__closeButton' onClick={props.closeOverlay}><Times /></span>
                </div>
                <div>
                    <span className='popUp_briefDesc'> {channelDescription} </span>
                </div>
                <FloatingLabelTextField 
                header={'Name'} 
                headerDesc={emptyFieldMessage} 
                beforeIcon={'#'} 
                placehoder={'e.g. plan-budget'} 
                onChange={props.onNameChange} 
                maxLength={80}
                />
                <FloatingLabelTextField 
                header={'Description'} 
                optional={true} 
                onChange={props.onDescChange} 
                beforeIcon={'#'}
                />
                <div>
                    <BasicButton disabled={props.disabled} label={'Create'}/>
                </div>
            </div>
        </div>
    );
}
