import React, {useState} from 'react';
import './App.css';
import { Login } from './components/Login/Login';
import { Main } from './Main';
import {
  BrowserRouter as Router,
  Switch,
  Route,
} from "react-router-dom";

export const App = () => {

  const [signIn, setSignIn] = useState(false);

  return (
    <div className="App">

      <Router>
        <Switch>
          <Route path="/">
            <Main />
          </Route>
          <Route path="/login">
            <Login />
          </Route>
        </Switch>
      </Router>
    </div>
  );
};
