import React, {useState} from 'react';

export const FloatingLabelTextField = (props) => {
    const [inputLength, setInputLength] = useState(0);

    const remainingChar = (event) => {
        const strLength = event.target.value.length;
        setInputLength(strLength);
    };

    return(
        <div onChange={remainingChar}>
            <span>{props.header}</span> {props.headerDesc !== undefined ? <span> </span> : null}
            <div className="floatingField_container">
                <span> {props.beforeIcon} </span>
                <input 
                placeholder='plan-project'
                onChange={props.onChange}
                > 
                </input>
                {props.maxlength !== null ? <span> {props.maxLength - inputLength} </span> : null}
            </div>
        </div>
    );
};
