import React, {useState} from 'react';

export const FloatingLabelTextField = (props) => {

    const [availableChar, setAvailableChar] = setState(props.maxLength)

    const remainingChar = (event) => {
        const currentLength = event.target.length;
        const remaining = availableChar - currentLength;
        setAvailableChar(remaining);
    };

    return(
        <div onChange={remainingChar}>
            <span>{props.header}</span> {props.headerDesc !== undefined ? <span> </span> : null}
            <div>
                <span> {props.beforeIcon} </span>
                <input placeholder='plan-project'> </input>
                {props.maxlength > 0 ? <span> {availableChar} </span> : null}
            </div>
        </div>
    );
};
