import React, {useState} from 'react';
import { InputField } from './InputField/InputField'
import { BoardHeader } from './Header/BoardHeader'
import './../../App.css';
import './../Main.css';

export const MessageBoard = () => {
    return(
        <div>
            <MessageSection />
        </div>
    );
};


const MessageSection = (props) => {
    return(
        <div>
            <UserIcon username={props.username}/>
            <ChatUserName />
            <UserMessages />
        </div>
    );
};

const  UserIcon = (props) => {
    const getFirstUserInitial = props.username.charAt(0);
    return (
        <div className="user-message-avatar">
            <span>A{getFirstUserInitial}</span>
        </div>
    );
}


const FirstMessage = (props) => {
    return(
        <div>

        </div>
    );
}

const TimeSeperator = (props) => {

    let shouldDisplay = false;

    const timeBetweenMessages = (props) => {

        const timeBetweenLastMessage = moment.duration(props.messageTime.diff(props.lastMessage, 'days'));

        if (timeBetweenLastMessage > 1) {
            shouldDisplay = true
        }
    }

    return(
        <div>
            {shouldDisplay ? <div>-- HERE WE WILL HAVE DATE SEPERATOR --</div> : null}

        </div>
    );
}
