import React from 'react';
import './Login.css';

export const Login = () => {
    return (
        <div class="login">
            <UsernameField></UsernameField>
            <PasswordField></PasswordField>
            <LoginButton></LoginButton>
            <SignUpButton></SignUpButton>
        </div>
    );
};

const UsernameField = () => {
    return (
        <input
        class='login__field'
        placeholder='Username/email'> 
        </input>
    )
};

const PasswordField = () => {
    return (
        <input
        type="password"
        class="login__field"
        placeholder='Password'> 
        </input>
    )
};

const LoginButton  = () => {
    return (
        <button class="login__button">
            Sign In
        </button>
    );
}

const SignUpButton = () => {
    return(
        <span>
            Don't have an account? <a>Create one now!</a>
        </span>
    )
}
