import React, {useState} from 'react';
import { Check } from './Icon';
import './../Main.css';

export const Toggle = (props) => {
    return(
        <div className={ props.selected ? 'toggle__container--selected ' : 'toggle__container' } onClick={props.onToggle}>
            <div className='toggle__check'> {props.selected ? <Check /> : ''} </div>
            <div className={ props.selected ? ' toggle__childSwitch toggle__childSwitch--selected toggle__container--selected ' : 'toggle__childSwitch' }>
            </div>
        </div>
    );
};
