import React, {useState} from 'react';
import './../../App.css';
import { PlusSign }  from './../shared/Icon.js';

export const ChannelSection = () => {

    const [channels, setChannels] = useState(['General', 'General test', 'General test', 'General test', 'General test', 'General test', 'General test', 'General test', 'General test', 'General test', 'General test', 'General test', 'General test']);
    const [name, setName] = useState('');
    const [showOverlay, setShowOverlay] = useState(false);

    const click = () => {
        setShowOverlay(true);
    };

    const close = () => {
        setShowOverlay(false);
    };

    const onChannelNameChange= (event) => {
        console.log(event.target.value);
        setName(event.target.value);
    };

    const onSubmit = () => {
        setChannels(channels.push(name));
        console.log(name)
        console.log(channels);
    };

    return(
        <div className="main__layout--channels">
            <CreateChannelButton channelsCount={channels.length} createChannelClick={click}/>
            <Channels channels={channels}/>
           { showOverlay ? <AddChannelOverlay closeOverlay={close} onChange={onChannelNameChange} onSubmit={onSubmit}/> : null }
        </div>
    );
};

const CreateChannelButton = (props) => {
    return (
        <div className="longButton__header">
            <span className="label__header label__header--title " > Channels ({props.channelsCount})</span> 
            <span className="label__header" onClick={props.createChannelClick}><PlusSign /></span>
        </div>
    );
};

const Channel = (props) => {
    return (
        <div className="section__channel containner_channel">
            {props.name} Channel
        </div>
    );
}


const Channels = (props) => {
const channelsAr = props.channels;
    return (
        <div className="section__channels">
            { channelsAr.map((aChannel) => 
                <Channel name={aChannel} />
            )}
        </div>
    );
}

const AddChannelOverlay = (props) => {
    return(
        <div className="section__overlay" >
            <div className="container__textField">
                <input
                type='text'
                className='login__field'
                placeholder='Channel Name'
                onChange={props.onChange}>
                </input>
                <input 
                type='text'
                className='login__field'
                placeholder='Subject (optional)'>
                </input>
                <div>
                    <span className="cancel__button" onClick={props.onSubmit}> Submit </span>
                </div>
                <div>
                    <span className="cancel__button" onClick={props.closeOverlay}> Cancel </span>
                </div>
            </div>
        </div>
    );
}
