import React, {useState} from 'react';
import './../../App.css';
import { PlusSign }  from './../shared/Icon.js';

export const ChannelSection = () => {
    return(
        <div className="main__layout--channels">
            <CreateChannelButton />
            <Channels />
        </div>
    );
};

const CreateChannelButton = (props) => {
    return (
        <div className="longButton__header">
            <span className="label__header" > Create New Channel </span> <PlusSign />
        </div>
    );
};

const Channel = (props) => {
    return (
        <div className="section__channel">
            {props.name} test
        </div>
    );
}


const Channels = (props) => {

    const [channels, setChannels] = useState(["1","2","3","4"]);

    return (
        <div className="section__channels">
            <ul>
                {channels.map((index, channel)=> 
                    <Channel key={index} name={channel} />
                )};
            </ul>
        </div>
    );
}
