import React, {useState} from 'react';
import './Login.css';
import { sign } from 'crypto';

export const Login = () => {

    const [signUp, setSignUp] = useState(false);

    const showSignUp = () => {
        return setSignUp(true)
    };

    return (
        <div class="login">
            <Slackish></Slackish> 
            {signUp ? <SignUpForm> showSignUp={showSignUp}</SignUpForm> : <LoginForm></LoginForm>}
        </div>
    );
};

const Slackish = () => {
    return (
        <div class="text__container">
            <span class='login__title'> #SLACKISH </span>
        </div>
    );
}

const LoginForm = (props) => {
    return (  
        <div>
            <UsernameField></UsernameField>
            <PasswordField></PasswordField>
            <LoginButton></LoginButton>
            <SignUpButton showSignUp={props.showSignUp}></SignUpButton>
        </div>
    );
}

const SignUpForm = () => {

};

const UsernameField = () => {
    return (
        <input
        class='login__field'
        placeholder='Username/email'> 
        </input>
    )
};

const PasswordField = () => {
    return (
        <input
        type="password"
        class="login__field"
        placeholder='Password'> 
        </input>
    )
};

const LoginButton  = () => {
    return (
        <button class="login__button login__button--email">
            Sign In
        </button>
    );
}

const SignUpButton = () => {

    return(
        <p>
            Don't have an account? <a class="link" onClick={props.showSignUp}>Create one now!</a>
        </p>
    )
}
